import React, { useState, useEffect } from 'react';
import Cards from '../components/Cards/Cards';

const Episodes = () => {

  let [id, setID] =useState(1);
  let[info, setInfo] = useState([]);
  let [results, setResults] = useState([]);
  let{air_date, name}=info;


  let api = `https://rickandmortyapi.com/api/episode/${id}`
  useEffect(() => {
    (async function () {
      let data = await fetch(api).then(res => res.json());
      setInfo(data);


      let a =await Promise.all(
        data.characters.map((x)=>{
          return fetch(x).then((res)=>res.json());
        })
      );
      setResults(a)
    })();
  }, [api]);
  return (
    <div className='container'>
      <div className='row'>
        <h1 className="text-center mb-3 ">Episode :{" "}<span className='text-success'>{name === ""? "Unknown":name}</span> </h1>
        <h1 className="text-center ">Air Date :{" "}<span className='text-success'>{air_date === ""? "Unknown":air_date}</span> </h1>
      </div>
      <div className='row'>
        <div className='col-3'>
          Pick Episodes
        </div>
        <div className='col-8'>
          <div className='row'><Cards results={results} />
          </div>
        </div>

      </div>



    </div>
  )
}

export default Episodes
