import React from 'react';
import styles from './Cards.module.scss';

const Cards = ({ results }) => {

    let display;
    console.log(results);

    if (results) {
        display = results.map((x) => {
            let { id, name, image, location, status } = x;
            return (
                <div key={id} className="col-4 mb-4 position-relative">
                    <div className={styles.cards}>
                        <img src={image} alt="" className={`${styles.img} img-fluid`} />
                        <div style={{padding:"10px"}} className='content'>
                            <div className='fs-4 fw-bold mb-3'>
                                {name}
                            </div>
                            <div className=''>
                                <div className='fs-6'>
                                    Last location
                                </div>
                                <div className='fs-5'>
                                     {location.name}
                                </div>
                            </div>
                        </div>
                    </div>
                    {(()=>{
                        if(status ==="Dead"){
                            return(
                                <div className={`${styles.badge} position-absolute badge bg-danger`}>{status}</div>
                            )
                        }
                        else if(status ==="Dead")
                        {
                            return(<div className={`${styles.badge} position-absolute badge bg-success`}>{status}</div>)
                            
                        }
                        else
                        {
                            return(<div className={`${styles.badge} position-absolute badge bg-secondary`}>{status}</div>)
                            
                        }
                    })};

                    <div className={`${styles.badge} position-absolute badge bg-success`}>{status}</div>

                </div>);
        });
    }
    else {
        display = "0 Character Found";
    }




    return (
        <>
            {display}
        </>
    )
}

export default Cards
